INSERT INTO points (name, description, img_url, longitude, latitude, map_id)  VALUES ('Royal Park', 'The forest was limitless, light, and lush. Its canopy was demanded by spruce, chestnut, and maple, sufficient twinkling lights bursted through their crowns for scattered saplings to take advantage of the nut and seed covered grounds below.', 'https://i.imgur.com/XmYVm7c.jpeg', -79.38, 43.65, 1);
INSERT INTO points (name, description, img_url, longitude, latitude, map_id)  VALUES ('Sunnyside Gardens', 'Curling branches hung from a couple of trees, and a mishmash of flowers, which were found in the most quiet places, stood out against the otherwise brown terrain.', 'https://i.imgur.com/CjdvGqJ.jpeg', -79.397507, 43.664002, 1);
INSERT INTO points (name, description, img_url, longitude, latitude, map_id)  VALUES ('Redrum Conservatory', 'The forest was endless, radiant, and primal. Its canopy was eclipsed by cypress, magnolia, and oak, their crowns allowed for short beams of light to descend for an overabundance of bushes to sprout', 'https://i.imgur.com/6GR1x1U.jpeg', -79.356480, 43.661021, 1);
